import './App.css';
//import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from "react-router-dom";

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="indice" element={<Indice />} />
          <Route path="agitador" element={<Agitador />} />
          <Route path="argamassadeira" element={<Argamassadeira />} />
          <Route path="balanca" element={<Balança />} />
          <Route path="banho" element={<BanhoMaria />} />
          <Route path="teste" element={<teste />} />
        </Routes>

      </header>
    </div>
  );
}

function Home() {
  return (
    <>
      <main>
      <div className='board-box'>
        <h2 className='title'>Olá</h2>
        <div className='text'>
          Para acessar os manuais dos maquinários, clique no botão abaixo. 
          Os manuais não são oficiais e podem conter erros. Em caso de dúvidas, contate um responsável.</div>
      </div>
      </main>
      <nav className='box'>
        <Link className="App-link" to="/indice">Clique Aqui!</Link>
      </nav>
    </>
  );
}

function Indice() {
  return (
    <>
      <nav className='box'>
        <Link className="App-link" to="/agitador">Agitador Mecânico</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/argamassadeira">Argamassadeira</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/balanca">Balança</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/banho">Banho Maria</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/">Voltar</Link>
      </nav>
    </>
  );
}

function Agitador() {
  return (
    <>
      <main video width="1000" height="1000" controls>
        <h2 className="Header">Primeiramente, o que é "vermelho"?</h2>
        <p className='text'>
          Insira sua mensagem aqui...<br /> Insira sua mensagem aqui... {/* O <br /> funciona como quebra de linha! */}
          Insira sua mensagem aqui...  <br /> Insira sua mensagem aqui...
          Insira sua mensagem aqui...<br />  Insira sua mensagem aqui...
          Insira sua mensagem aqui... <br /> Insira sua mensagem aqui... <br />Insira sua mensagem aqui...
        </p>
      </main>

      <nav className='box'>
        <Link className="App-link" to="/argamassadeira">Avançar</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/indice">Voltar</Link>
      </nav>
    </>
  );
}

function Argamassadeira() {
  return (
    <>
      <main>
        <p className="Header">Insira sua mensagem aqui...</p>
      </main>
      <nav className='box'>
        <Link className="App-link" to="/balanca">Avançar</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/agitador">Voltar</Link>
      </nav>
    </>
  );
}

function Balança() {
  return (
    <>
      <main>
        <h2 className="Header"> Isso ainda é um cartão?</h2>

        <iframe className='video'
        width="853" 
        height="480" 
        src="https://www.youtube.com/embed/tollGa3S0o8" // esse é o código incorporado do vídeo, para você utilizar qualquer vídeo. Entre no youtube, clique com o botão direito no vídeo e "copiar código incorporado".
        title="YouTube video player" 
        frameborder="0" allow="accelerometer; autoplay; clipboard-write; 
        encrypted-media; gyroscope; picture-in-picture">
        </iframe>
        <p className='text1'>  Insira sua mensagem aqui...<br /> Insira sua mensagem aqui... {/* O <br /> funciona como quebra de linha! */}
          Insira sua mensagem aqui...  <br /> Insira sua mensagem aqui...
          Insira sua mensagem aqui...<br />  Insira sua mensagem aqui...
          Insira sua mensagem aqui... <br /> Insira sua mensagem aqui... <br />Insira sua mensagem aqui...
          <br /><br />
          Me siga nas redes sociais e me marca se você fez!! <br />
          Twitter: @aryzete_ <br />
          Instagram: @aryzete_ <br />
          Twitch: @aryzete <br />
          GitHub: @aryniceia <br />
        </p>

      </main>
      <nav className='box' >
        <Link className="App-link" to="/Page">Voltar</Link>
      </nav>
    </>
  );
}

function BanhoMaria() {
  return (
    <>
      <main>
        <p className="Header">Insira sua mensagem aqui...</p>
      </main>
      <nav className='box'>
        <Link className="App-link" to="/Musiquinha">Avançar</Link>
      </nav>
      <nav className='box'>
        <Link className="App-link" to="/About">Voltar</Link>
      </nav>
    </>
  );
}

export default App;
